export default {
  computed: {
    projects: function projects() {
      return this.$store.getters.projects;
    },
    teamMembers: function teamMembers() {
      return this.$store.getters.teamMembers;
    },
    noteIsValid: function noteIsValid() {
      return this.note.teamMemberId && this.note.project && this.note.workYesterday && this.note.workToday;
    }
  },
  data: function data() {
    return {
      dialog: null,
      note: {
        teamMemberId: null,
        teamMember: null,
        project: null,
        workYesterday: null,
        workToday: null,
        impediment: null
      }
    };
  },
  methods: {
    saveStandup: function saveStandup() {
      // Get the team member's name from their Id...
      this.note.teamMember = this.getTeamMemberNameById(this.note.teamMemberId);
      this.$store.dispatch('saveStandup', this.note);
      this.dialog = false;
    },
    getTeamMemberNameById: function getTeamMemberNameById(id) {
      var tm = this.teamMembers.find(function (tm) {
        return tm._id == id;
      });

      if (tm) {
        return tm.name;
      }

      return '';
    }
  }
};
